import React, {Component, Fragment} from 'react';
import './Product.css';

class Product extends Component {
   state = {
      order: 4
   }

   handlePlus=() =>{
      // alert('Plus Button')
      this.setState = {
         order: this.state.order + 1,
      }
   }

   handleMinus() {
      // alert('Minus Button')
      this.setState = {
         order: this.state.order - 1,
      }
   }

   render () {
      return (
         <Fragment>
            <div className='header'>
               <div className='logo'>
                  <img src='https://etanee.id/img/content/img_ayam.jpg' alt='gambar 1'/>
               </div>
               <div className='troley'>
                  {/* <img src='https://etanee.id/img/content/img_sapi.jpg' alt='gambar 2'/> */}
                  <div className='count'>{this.state.order}</div>
               </div>
            </div>
            <div className='card'>
               <div className='img-thumb-prod'>
                  {/* <img src='https://etanee.id/img/content/img_ikan.jpg' alt='gambar 3'/> */}
               </div>
               <p className='product-title'>Daging Ayam berbumbu original plus tepung crispy [1 karton - 10 pak]</p>
               <p className='product-price'>Rp. 410,000</p>
               <div className='counter'>
                  <button className='minus' onClick={this.handleMinus}>-</button>
                  <input type='text' value={this.state.order} />
                  <button className='plus' onClick={this.handlePlus}>+</button>
               </div>
            </div>
         </Fragment>
      )
   }
}

export default Product;
